import { Flex, ScrollView, Text, Image, Box, Heading } from 'native-base';
import React from 'react';
import { Pressable } from 'react-native';
import Colors from '../color';
import products from '../data/Products';

function HomeProducts() {
  return (
    <ScrollView flex={1}>
        <Flex flexWrap="wrap" direction="row" justifyContent="space-between" px={6}> 
            {products.map((product) => (
                <Pressable key={product._id} w="%" bg={Colors.white} 
                   rounded="md" shadow={2} pt={0.3} my={3} pb={2} overflow="hidden">
                    <Box>
                        <Image source={{uri:product.image}} alt={product.name} w="full"
                         h={24} resizeMode="contain"/>
                        <Box px={4} pt={1}>
                            <Heading size="sm" bold>
                                ${product.price}
                            </Heading>
                            <Text fontSize={10} mt={1} isTruncated w="full">
                                {product.name}
                            </Text>
                        </Box>
                    </Box>
                </Pressable>
            ))}
        </Flex>
    </ScrollView>
  );
}

export default HomeProducts;
