import React, { useEffect, useState } from "react";
import {
  Text,
  Box,
  ScrollView,
  Image,
  Button,
  Center,
  HStack,
  View,
} from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";
import SlideScreen from "../Components/SlideScreen";
import { ActivityIndicator, RefreshControl } from "react-native";
import axios from "axios";
import { Storage } from "expo-storage";
const wait = (timeout) => {
  return new Promise((resolve) => setTimeout(resolve, timeout));
};

const categories = [
  {
    label: "Xe cộ",
    image: require("../../assets/xeco.png"),
  },
  {
    label: "Giải trí, thể thao",
    image: require("../../assets/giaitri.png"),
  },
  {
    label: "Đồ gia dụng, nội thất, cây cảnh",
    image: require("../../assets/noithat.jpg"),
  },
  {
    label: "Thời trang",
    image: require("../../assets/thoitrang.jpg"),
  },
  {
    label: "Tủ lạnh, máy lạnh",
    image: require("../../assets/tulanh.jpg"),
  },
  {
    label: "Đồ điện tử",
    image: require("../../assets/dodientu.jpg"),
  },
];
const Url = `http://192.168.1.3:5000`;

function HomeScreen() {
  const [pro, setPro] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchProducts = async () => {
    try {
      const { data } = await axios.get(`${Url}/api/product/show/accept`);
      setPro(data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchProducts();
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  }, []);

  const [refreshing, setRefreshing] = React.useState(false);
  const onRefresh = React.useCallback(() => {
    setRefreshing(true);
    setLoading(true);
    fetchProducts();
    wait(1000).then(() => {
      setRefreshing(false);
      setLoading(false);
    });
  }, []);
  return (
    <Box bg={Colors.gray} flex={1}>
      <Input
        placeholder="Tìm kiếm "
        w="85%"
        size={10}
        pl={2}
        bg={Colors.white}
        flex={1}
        type="search"
        h={9}
        borderWidth={0}
        _focus={{ bg: Colors.white }}
        onChangeText={(e) => {
          e = e.replace(/  +/g, " ");
          setSearch(e.replace(/^[ \t]+|[ \t]+$/gm, ""));
        }}
        onEndEditing={() => {
          setLoading(true);
          fetchProducts();
          wait(500).then(() => {
            setLoading(false);
          });
        }}
        variant="filled"
      />
      <ScrollView
        showsVerticalScrollIndicator={false}
        refreshControl={
          <RefreshControl refreshing={refreshing} onRefresh={onRefresh} />
        }
      >
        <View
          bg={Colors.white}
          paddingLeft={3}
          paddingRight={3}
          paddingBottom={3}
        >
          <Select
            minWidth="200"
            accessibilityLabel="Chọn danh mục"
            placeholder="Chọn danh mục"
            _selectedItem={{
              bg: "teal.600",
              endIcon: <CheckIcon size="5" />,
            }}
            mt={1}
            onValueChange={(itemValue) => {
              if (itemValue === "Tất cả") {
                setCategory("");
              } else {
                setCategory(itemValue);
              }
            }}
          >
            <Select.Item label="Tất cả" value="Tất cả" />
            {categories.map((data) => (
              <Select.Item label={data} value={data} />
            ))}
          </Select>

          <Select
            accessibilityLabel="Chọn thành phố"
            placeholder="Chọn thành phố"
            _selectedItem={{
              bg: "teal.600",
              endIcon: <CheckIcon size="5" />,
            }}
            mt={1}
            onValueChange={(itemValue) => {
              if (itemValue === "Tất cả") {
                setCity("");
              } else {
                setCity(itemValue);
              }
            }}
          >
            <Select.Item label="Tất cả" value="Tất cả" />
            {cities.map((data) => (
              <Select.Item label={data} value={data} />
            ))}
          </Select>
          <View mt={3}>
            <Button
              bg={"#FFA54F"}
              _pressed={{ bg: "#FFA54F" }}
              onPress={() => {
                setLoading(true);
                fetchProducts();
                wait(500).then(() => {
                  setLoading(false);
                });
              }}
            >
              <Text color={"white"}>Áp dụng</Text>
            </Button>
          </View>
        </View>
        {loading ? (
          <View flex={1} justifyContent="center" alignItems="center">
            <ActivityIndicator size="large" />
          </View>
        ) : pro.length > 0 ? (
          <Box bg={Colors.white} mt={2} pb={5}>
            <HomeProducts products={pro} />
          </Box>
        ) : (
          <View>
            <Text>Ko có</Text>
          </View>
        )}
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
