import axios from "axios";
const URL = `http://192.168.1.3:5000`;
export const addBlog =
  (
    userId,
    category,
    title,
    image,
    image1,
    image2,
    description,
    price,
    isNew,
    city,
    district,
    ward
  ) =>
  async () => {
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      };

      const { data } = await axios.post(
        `${URL}/api/product`,
        {
          userId,
          category,
          title,
          image,
          image1,
          image2,
          description,
          price,
          isNew,
          city,
          district,
          ward,
        },
        config
      );
    } catch (error) {
      console.log(error);
    }
  };

export const updateStatusBlog = (id, status) => async () => {
  try {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const { data } = await axios.put(
      `${URL}/api/product/update-status`,
      { id, status },
      config
    );
  } catch (error) {
    const message =
      error.response && error.response.data.message
        ? error.response.data.message
        : error.message;
  }
};

export const updateBlog = (id, status) => async () => {
  try {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const { data } = await axios.put(
      `${URL}/api/product/update-blog`,
      { id, status },
      config
    );
  } catch (error) {
    const message =
      error.response && error.response.data.message
        ? error.response.data.message
        : error.message;
  }
};
