import { NativeBaseProvider, StatusBar, View } from "native-base";
import { NavigationContainer } from "@react-navigation/native";
import { createNativeStackNavigator } from "@react-navigation/native-stack";
import BottomNav from "./src/Navigations/BottomNav";
import LoginScreen2 from "./src/Screens/LoginScreen2";
import RegisterScreen2 from "./src/Screens/RegisterScreen2";
import SimiliarProductScreen from "./src/Screens/SimiliarProductScreen";
import UpdateProfileScreen from "./src/Screens/UpdateProfileScreen";
import React, { useEffect, useMemo, useState } from "react";
import { ActivityIndicator } from "react-native";
import { AuthContext } from "./src/Components/AuthContext";

const Stack = createNativeStackNavigator();

export default function App() {
  // const [isLoading, setIsLoading] = useState(true);
  // const [userToken, setUserToken] = useState(null);

  const initialLoginState = {
    isLoading: true,
    userToken: null,
  };

  const loginReducer = (prevState, action) => {
    switch (action.type) {
      case "LOGIN":
        return {};
      case "LOGIN":
        return {};
      case "LOGIN":
        return {};
      case "LOGIN":
        return {};
    }
  };

  const authContext = useMemo(
    () => ({
      logIn: () => {
        setUserToken("adwawdada");
        setIsLoading(false);
      },
      logOut: () => {
        setUserToken(null);
        setIsLoading(false);
      },
      signUp: () => {
        setUserToken("adwawdada");
        setIsLoading(false);
      },
    }),
    []
  );

  useEffect(() => {
    setTimeout(() => {
      setIsLoading(false);
    }, 1000);
  }, []);

  if (isLoading) {
    return (
      <NativeBaseProvider>
        <View flex={1} justifyContent="center" alignItems="center">
          <ActivityIndicator size="large" />
        </View>
      </NativeBaseProvider>
    );
  }

  return (
    <AuthContext.Provider value={authContext}>
      <NativeBaseProvider>
        <NavigationContainer>
          <StatusBar hidden={true} />
          {userToken != null ? (
            <Stack.Navigator
              initialRouteName="Bottom"
              screenOptions={{
                headerShown: false,
              }}
            >
              <Stack.Screen name="Login" component={LoginScreen2} />
              <Stack.Screen name="Register" component={RegisterScreen2} />
              <Stack.Screen name="Bottom" component={BottomNav} />
              <Stack.Screen
                name="SimiliarProduct"
                component={SimiliarProductScreen}
              />
              <Stack.Screen
                name="UpdateProfile"
                component={UpdateProfileScreen}
              />
            </Stack.Navigator>
          ) : (
            <LoginScreen2 />
          )}
        </NavigationContainer>
      </NativeBaseProvider>
    </AuthContext.Provider>
  );
}
