import {
  Box,
  Text,
  ScrollView,
  View,
  VStack,
  Input,
  HStack,
  FormControl,
  WarningOutlineIcon,
  Icon,
} from "native-base";
import React, { useEffect, useState } from "react";
import Colors from "../color";
import { ActivityIndicator, Pressable } from "react-native";
import { useDispatch, useSelector } from "react-redux";
import { updateAvatar } from "../Redux/Actions/UserActions";
import axios from "axios";
import CustomButton from "../Components/CustomButton";
import { Ionicons, MaterialIcons } from "@expo/vector-icons";
import { Storage } from "expo-storage";
function ChangePasswordScreen({ navigation }) {
  const userLogin = useSelector((state) => state.userLogin);
  const [loading, setLoading] = useState(false);
  const [show, setShow] = React.useState(false);
  const [show1, setShow1] = React.useState(false);
  const [show2, setShow2] = React.useState(false);
  const { userInfo } = userLogin;
  const [oldpass, setOldPass] = useState(null);
  const [newPass, setNewPass] = useState(null);
  const [confirm, setConfirm] = useState(null);
  const [avatar, setAvatar] = useState(null);
  const [pw, setPw] = useState(null);
  const pass = async () => {
    const getPass = await Storage.getItem({ key: "pass" });
    setPw(getPass);
  };

  const validatePass = (pass) => {
    let reg = /^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d@$!%*#?&]{8,}$/;
    if (reg.test(pass) === false) {
      setNewPass(null);
    } else {
      setNewPass(pass);
    }
  };
  useEffect(() => {
    pass();
  }, []);

  const dispatch = useDispatch();
  const submitHandler = async () => {
    setLoading(true);
    dispatch(updateAvatar(userInfo._id, avatar, newPass));
    const setPass = await Storage.setItem({ key: "pass", value: newPass });
    setTimeout(() => {
      setLoading(false);
      navigation.goBack();
    }, 1000);
  };
  return (
    <Box safeAreaTop bg={Colors.gray}>
      <HStack
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
        safeAreaTop
      >
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Thay đổi mật khẩu
        </Text>
      </HStack>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View bg={Colors.white} mt={5}>
          <VStack p={5}>
            <FormControl>
              <Input
                fontSize={15}
                placeholder="Mật khẩu cũ"
                mt={2}
                type={show ? "text" : "password"}
                InputRightElement={
                  <Pressable onPress={() => setShow(!show)}>
                    <Icon
                      as={
                        <MaterialIcons
                          name={show ? "visibility" : "visibility-off"}
                        />
                      }
                      size={5}
                      mr="2"
                      color="muted.400"
                    />
                  </Pressable>
                }
                onChangeText={(e) => setOldPass(e)}
              />
              {pw != oldpass && oldpass != null ? (
                <FormControl.HelperText>
                  <Text color="red.400" fontSize={13}>
                    Mật khẩu không đúng
                  </Text>
                </FormControl.HelperText>
              ) : (
                <View></View>
              )}

              <Input
                fontSize={15}
                placeholder="Mật khẩu mới"
                mt={2}
                onChangeText={(e) => validatePass(e)}
                type={show1 ? "text" : "password"}
                InputRightElement={
                  <Pressable onPress={() => setShow1(!show1)}>
                    <Icon
                      as={
                        <MaterialIcons
                          name={show1 ? "visibility" : "visibility-off"}
                        />
                      }
                      size={5}
                      mr="2"
                      color="muted.400"
                    />
                  </Pressable>
                }
              ></Input>
              {newPass == null ? (
                <FormControl.HelperText>
                  Ít nhất 8 kí tự, ít nhất 1 kí tự số và 1 kí tự chữ
                </FormControl.HelperText>
              ) : (
                <View></View>
              )}

              <Input
                fontSize={15}
                placeholder="Nhập lại mật khẩu"
                mt={2}
                onChangeText={(e) => {
                  setConfirm(e);
                }}
                type={show2 ? "text" : "password"}
                InputRightElement={
                  <Pressable onPress={() => setShow2(!show2)}>
                    <Icon
                      as={
                        <MaterialIcons
                          name={show2 ? "visibility" : "visibility-off"}
                        />
                      }
                      size={5}
                      mr="2"
                      color="muted.400"
                    />
                  </Pressable>
                }
              ></Input>
              {confirm != newPass && confirm != null ? (
                <FormControl.HelperText>
                  <Text color="red.400" fontSize={13}>
                    Mật khẩu không khớp
                  </Text>
                </FormControl.HelperText>
              ) : (
                <View></View>
              )}
            </FormControl>

            <View mt={5}>
              {oldpass === pw && newPass != null && confirm === newPass ? (
                <CustomButton
                  label={"CẬP NHẬP"}
                  bg="#F4A460"
                  disabled={false}
                  onPress={() => submitHandler()}
                />
              ) : (
                <CustomButton label={"CẬP NHẬP"} bg="gray" disabled={true} />
              )}
              {loading ? <Text>Dang cap nhap</Text> : ""}
              <ActivityIndicator size="large" />
            </View>
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default ChangePasswordScreen;
