import { Button, Image, Text, View } from "native-base";
import { useState } from "react";
import { StyleSheet } from "react-native";
import { StorageClient } from "@supabase/storage-js";
import * as ImagePicker from "expo-image-picker";

function TestScreen() {
  const [uploading, setUploading] = useState(false);
  const [avatarUrl, setAvatarUrl] = useState(null);
  const avatarSize = { height: 150, width: 150 };
  const [image, setImage] = useState(null);

  const STORAGE_URL = "https://ngfxbagkjhxyputmwoxp.supabase.co/storage/v1";
  const SERVICE_KEY =
    "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Im5nZnhiYWdramh4eXB1dG13b3hwIiwicm9sZSI6InNlcnZpY2Vfcm9sZSIsImlhdCI6MTY3MDIyMDE2NSwiZXhwIjoxOTg1Nzk2MTY1fQ.v1Bml2eA_eQno7gMBfDbtgLiyovkVs9orOz1w_89aVI"; //! service key, not anon key

  const storageClient = new StorageClient(STORAGE_URL, {
    apikey: SERVICE_KEY,
    Authorization: `Bearer ${SERVICE_KEY}`,
  });
  async function get() {
    const { data, error } = await storageClient.emptyBucket("image");
    console.log(data, error);
  }
  async function uploadAvatar() {
    try {
      setUploading(true);

      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.All,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });

      console.log(result);

      const photo = {
        uri: result.assets[0].uri,
        type: result.assets[0].type,
        name: "haha",
      };

      const formData = new FormData();
      formData.append("file", photo);

      const fileExt = photo.name.split(".").pop();
      const filePath = `${Math.random()}.${fileExt}`;

      const { data, error } = await storageClient
        .from("image")
        .upload("path/to/file", formData);

      if (error) {
        throw error;
      }
      console.log(data);

      if (!result.canceled) {
        setAvatarUrl(result.assets[0].uri);
      }
    } catch (error) {
      console.log(error);
    } finally {
      setUploading(false);
    }
  }

  return (
    <View>
      {avatarUrl ? (
        <Image
          source={{ uri: avatarUrl }}
          alt="a"
          accessibilityLabel="Avatar"
          style={[avatarSize, styles.avatar, styles.image]}
        />
      ) : (
        <View style={[avatarSize, styles.avatar, styles.noImage]} />
      )}
      <View>
        <Button
          title={uploading ? "Uploading ..." : "Upload"}
          onPress={() => uploadAvatar()}
          disabled={uploading}
        >
          Up
        </Button>
        <Button
          title={uploading ? "Uploading ..." : "Upload"}
          onPress={() => get()}
          disabled={uploading}
        >
          GET
        </Button>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  avatar: {
    borderRadius: 5,
    overflow: "hidden",
    maxWidth: "100%",
  },
  image: {
    objectFit: "cover",
    paddingTop: 0,
  },
  noImage: {
    backgroundColor: "#333",
    border: "1px solid rgb(200, 200, 200)",
    borderRadius: 5,
  },
});
export default TestScreen;
