import axios from "axios";
const URL = `http://192.168.1.7:5000`;
export const createOrder =
  (
    buyerId,
    sellerId,
    blogId,
    name,
    phone,
    city,
    district,
    ward,
    total,
    note,
    timeCreated
  ) =>
  async () => {
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      };

      const { data } = await axios.post(
        `${URL}/api/order`,
        {
          buyerId,
          sellerId,
          blogId,
          name,
          phone,
          city,
          district,
          ward,
          total,
          note,
          timeCreated,
        },
        config
      );
    } catch (error) {
      console.log(error);
    }
  };
