import React from "react";
import { View, Text, Box, Center, ScrollView, Image } from "native-base";
import { TouchableOpacity } from "react-native";
import Colors from "../color";
import products from "../data/Products";
function SimiliarProducts() {
  return (
    <View pb={10}>
      <View
        flexDirection="row"
        justifyContent="space-between"
        borderBottomColor="#B5B5B5"
        borderBottomWidth={0.2}
        pb={2}
      >
        <Text bold>Tin đăng tương tự</Text>
        <TouchableOpacity>
          <Text bold color="#436EEE">
            Xem tất cả {">"}
          </Text>
        </TouchableOpacity>
      </View>
      <ScrollView horizontal showsHorizontalScrollIndicator={false}>
        {products.map((i, index) => (
          <TouchableOpacity alignItems="center" w={100}>
            <Center>
              <Image
                source={{
                  uri: "https://cdn.chotot.com/DuVpu6i8MOw3_TGascyKXrSUwj2nnoUtLhepotFyQGM/preset:view/plain/b36155e6b0167d3d9879a606299e5c80-2800565900077283465.jpg",
                }}
                alt="product"
                h={70}
                flex={1}
                w={70}
                resizeMode="contain"
                rounded={3}
              />
            </Center>
            <Text flex={1} numberOfLines={2} isTruncated>
              Nước hoa nam Luxury Joseph
            </Text>
            <Text color={Colors.red}>
              1.250.000 <Text underline>đ</Text>
            </Text>
          </TouchableOpacity>
        ))}
      </ScrollView>
    </View>
  );
}

export default SimiliarProducts;
