import {
  Box,
  Text,
  ScrollView,
  HStack,
  Pressable,
  View,
  Image,
  VStack,
  Input,
  TextArea,
} from "native-base";
import React, { useEffect, useState } from "react";
import Colors from "../color";
import { Ionicons, Entypo } from "@expo/vector-icons";
import { ActivityIndicator, TouchableOpacity } from "react-native";
import { addBlog } from "../Redux/Actions/ProductActions";
import { useDispatch } from "react-redux";
import { Storage } from "expo-storage";
import * as ImagePicker from "expo-image-picker";

function CreateBlogScreen({ navigation, route }) {
  const [loading, setLoading] = useState(false);
  const [id, setID] = useState("");
  const [New, setNew] = useState(false);
  const category = route.params;
  const [image, setImage] = useState("");
  const [image1, setImage1] = useState("");
  const [image2, setImage2] = useState("");
  const [price, setPrice] = useState("");
  const [title, setTitle] = useState("");
  const [des, setDes] = useState("");
  const [city, setCity] = useState("");
  const [dis, setDis] = useState("");
  const [ward, setWard] = useState("");

  const updateImage = async (index) => {
    try {
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });
      if (!result.canceled) {
        const ext = result.assets[0].uri.substring(
          result.assets[0].uri.lastIndexOf(".") + 1
        );
        if (index === 1) setImage(result.assets[0].uri);
        else if (index === 2) setImage1(result.assets[0].uri);
        else setImage2(result.assets[0].uri);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const dispatch = useDispatch();
  const addBlogHandler = () => {
    setLoading(true);
    dispatch(
      addBlog(
        id,
        category,
        title,
        image,
        image1,
        image2,
        des,
        price,
        New,
        city,
        dis,
        ward
      )
    );
    setTimeout(() => {
      setLoading(false);
      navigation.goBack();
    }, 1000);
  };
  const userID = async () => {
    const uid = await Storage.getItem({ key: "userID" });
    setID(uid);
  };
  useEffect(() => {
    userID();
  }, []);
  return (
    <Box safeAreaTop bg={Colors.white}>
      {/* Header */}
      <HStack space={3} w="full" px={4} bg="#FFA500" py={3} alignItems="center">
        <Pressable onPress={() => navigation.goBack()}>
          <Ionicons name="arrow-back" size={24} color="white" />
        </Pressable>
        <Text bold fontSize={17} color="white">
          Đăng tin
        </Text>
      </HStack>
      {/* Body */}
      <ScrollView showsVerticalScrollIndicator={false} mt={5}>
        <View bg={Colors.white} w="full" px={3}>
          <Text fontSize={13} color="gray.500">
            Danh mục
          </Text>
          <Pressable
            mt={1}
            borderWidth={1}
            p={3}
            rounded={5}
            flexDirection="row"
            justifyContent="space-between"
            borderColor="gray.400"
            onPress={() => {
              navigation.goBack();
            }}
          >
            <Text>{category}</Text>
            <Ionicons
              name="chevron-forward"
              size={20}
              color={Colors.deepestGray}
              style={{ alignSelf: "center" }}
            />
          </Pressable>
        </View>
        <VStack mt={5} px={3}>
          <View>
            <Text fontSize={17} color="gray.400">
              THÔNG TIN CHI TIẾT
            </Text>
          </View>
          <View mt={5}>
            <Pressable borderWidth={0.5} alignItems="center">
              <Entypo
                name="camera"
                size={15}
                color="white"
                style={{
                  backgroundColor: "gray",
                  borderRadius: 20,
                  padding: 5,
                }}
              />
              <Text>Chọn hình ảnh bìa</Text>
            </Pressable>
            <Input
              placeholder="Link Ảnh 1"
              borderColor={"gray.500"}
              onChangeText={(e) => setImage1(e)}
              mt={3}
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
            <Input
              placeholder="Link Ảnh 2"
              borderColor={"gray.500"}
              mt={3}
              onChangeText={(e) => setImage2(e)}
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
          </View>
          <Text mt={5} color={"gray.500"}>
            Tình trạng
          </Text>
          <View mt={2} flexDirection="row">
            <Pressable
              borderWidth={0.5}
              p={2}
              rounded={10}
              borderColor="white"
              bg={New === false ? "#FFEC8B" : "gray.200"}
              onPress={() => setNew(false)}
            >
              <Text color={New === false ? "#FFA500" : "black"}>
                Đã sử dụng
              </Text>
            </Pressable>
            <Pressable
              ml={5}
              borderWidth={0.5}
              p={2}
              rounded={10}
              borderColor="white"
              bg={New === false ? "gray.200" : "#FFEC8B"}
              w={50}
              alignItems="center"
              onPress={() => setNew(true)}
            >
              <Text color={New === false ? "black" : "#FFA500"}>Mới</Text>
            </Pressable>
          </View>
          <View mt={5}>
            <Input
              placeholder="Giá"
              borderColor={"gray.500"}
              keyboardType="numeric"
              onChangeText={(e) => setPrice(e)}
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
            <Input
              placeholder="Tiêu đề"
              borderColor={"gray.500"}
              mt={3}
              onChangeText={(e) => setTitle(e)}
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
            <TextArea
              mt={3}
              h={20}
              placeholder="Mô tả chi tiết"
              borderColor={"gray.500"}
              onChangeText={(e) => setDes(e)}
              w="100%"
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
          </View>
          <View mt={5}>
            <Text fontSize={17} color="gray.400">
              VỀ NGƯỜI BÁN
            </Text>
          </View>
          <View mt={2}>
            <Input
              placeholder="Thành phố"
              borderColor={"gray.500"}
              mt={3}
              onChangeText={(e) => setCity(e)}
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
            <Input
              placeholder="Quận/huyện"
              borderColor={"gray.500"}
              mt={3}
              onChangeText={(e) => setDis(e)}
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
            <Input
              placeholder="Phường/Xã"
              borderColor={"gray.500"}
              onChangeText={(e) => setWard(e)}
              mt={3}
              _focus={{ borderColor: "gray.500", bg: "white" }}
            />
          </View>
          <View mb={100} alignItems="center" mt={5}>
            {loading === true ? (
              <Pressable
                style={{
                  borderWidth: 1,
                  borderRadius: 10,
                  padding: 10,
                  width: 150,
                  alignItems: "center",
                  backgroundColor: "gray",
                  borderColor: "white",
                }}
              >
                <ActivityIndicator size="small" />
              </Pressable>
            ) : image.trim() != "" &&
              image1.trim() != "" &&
              image2.trim() != "" &&
              price.trim() != "" &&
              title.trim() != "" &&
              des.trim() != "" &&
              city.trim() != "" &&
              dis.trim() != "" &&
              ward.trim() != "" ? (
              <TouchableOpacity
                style={{
                  borderWidth: 1,
                  borderRadius: 10,
                  padding: 10,
                  width: 150,
                  alignItems: "center",
                  backgroundColor: "#FFA500",
                  borderColor: "white",
                }}
                onPress={() => {
                  addBlogHandler();
                }}
              >
                <Text color="white" bold>
                  ĐĂNG TIN
                </Text>
              </TouchableOpacity>
            ) : (
              <Pressable
                style={{
                  borderWidth: 1,
                  borderRadius: 10,
                  padding: 10,
                  width: 150,
                  alignItems: "center",
                  backgroundColor: "gray",
                  borderColor: "white",
                }}
              >
                <Text color="white" bold>
                  ĐĂNG TIN
                </Text>
              </Pressable>
            )}
          </View>
        </VStack>
      </ScrollView>
    </Box>
  );
}

export default CreateBlogScreen;
