import {
  Box,
  Text,
  ScrollView,
  HStack,
  View,
  Pressable,
  Image,
  VStack,
} from "native-base";
import React, { useState } from "react";
import Colors from "../color";
import { Ionicons } from "@expo/vector-icons";
import Rating from "../Components/Rating";

function ProfileScreen2({ navigation }) {
  return (
    <Box safeAreaTop bg={Colors.gray}>
      <View
        space={3}
        w="full"
        px={4}
        bg="#FFA500"
        py={3}
        alignItems="center"
      >
        <Text bold fontSize={17} color="white">
          Thông tin cá nhân
        </Text>
      </View>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View flexDirection="row" bg={Colors.white} w="full" px={3} py={3} alignItems="center">
          <Image
            source={require("../../assets/avatar.jpg")}
            h={20}
            w={20}
            alt="avatar"
            rounded="full"
          />
          <Text bold fontSize={17} ml={5}>
              Trần Lý Thắng
          </Text>
          {/* <VStack ml={5}>
            <Text bold fontSize={17}>
              Trần Lý Thắng
            </Text>
            <Text
              borderWidth={1}
              rounded="full"
              alignItems="center"
              textAlign="center"
              mt={2}
              bg={Colors.black}
              color={Colors.white}
            >
              Thành viên
            </Text>
          </VStack> */}
        </View>
      </ScrollView>
    </Box>
  );
}

export default ProfileScreen2;
