import {
  Box,
  Text,
  ScrollView,
  HStack,
  View,
  Pressable,
  Image,
  VStack,
} from "native-base";
import React, { useState } from "react";
import Colors from "../color";
import { Ionicons } from "@expo/vector-icons";
import Rating from "../Components/Rating";
import { TouchableOpacity } from "react-native";

function ProfileScreen2({ navigation }) {
  return (
    <Box safeAreaTop bg={Colors.gray}>
      <View space={3} w="full" px={4} bg="#FFA500" py={3} alignItems="center">
        <Text bold fontSize={17} color="white">
          Thông tin cá nhân
        </Text>
      </View>
      <ScrollView showsVerticalScrollIndicator={false}>
        <View
          flexDirection="row"
          bg={Colors.white}
          w="full"
          px={3}
          py={3}
          alignItems="center"
        >
          <Image
            source={require("../../assets/avatar.jpg")}
            h={20}
            w={20}
            alt="avatar"
            rounded="full"
          />
          <VStack ml={5}>
            <Text bold fontSize={17}>
              Trần Lý Thắng
            </Text>
            <TouchableOpacity
              style={{
                borderWidth: 1,
                borderRadius: 25,
                alignItems: "center",
                textAlign: "center",
                marginTop: 2,
                color: Colors.white,
                borderColor: Colors.deepestGray,
                padding: 5,
                paddingRight: 10,
                paddingLeft: 10,
              }}
            >
              <Text fontSize={12}>Chỉnh sửa trang cá nhân</Text>
            </TouchableOpacity>
          </VStack>
        </View>
        <Text color="#808080" bold fontSize={15} p={3}>Quản lí đơn hàng</Text>
        <View bg={Colors.white}>
          <VStack>
            <TouchableOpacity style={{flex:1, flexDirection: "row", alignItems: "center", padding: 10,borderBottomWidth: 0.6, borderBottomColor: Colors.deepGray}}>
              <Image
                source={require("../../assets/icon1.png")}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>Đơn mua</Text>
            </TouchableOpacity>
            <TouchableOpacity style={{flex:1, flexDirection: "row", alignItems: "center", padding: 10}}>
              <Image
                source={require("../../assets/icon2.png")}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>Đơn bán</Text>
            </TouchableOpacity>
            
          </VStack>
        </View>
        <Text color="#808080" bold fontSize={15} p={3}>Tiện ích</Text>
        <View bg={Colors.white} mb={20}>
          <VStack>
            <TouchableOpacity style={{flex:1, flexDirection: "row", alignItems: "center", padding: 10,borderBottomWidth: 0.6, borderBottomColor: Colors.deepGray}}>
              <Image
                source={require("../../assets/icon3.png")}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>Tin đăng đã lưu</Text>
            </TouchableOpacity>
            <TouchableOpacity style={{flex:1, flexDirection: "row", alignItems: "center", padding: 10}}>
              <Image
                source={{
                  uri: "https://cdn-icons-png.flaticon.com/512/1827/1827887.png",
                }}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>Cài đặt tài khoản</Text>
            </TouchableOpacity>
            <TouchableOpacity style={{flex:1, flexDirection: "row", alignItems: "center", padding: 10}}>
              <Image
                source={{
                  uri: "https://cdn-icons-png.flaticon.com/512/7756/7756285.png",
                }}
                alt="icon"
                h={7}
                w={7}
              />
              <Text fontSize={15} ml={2}>Đăng xuất</Text>
            </TouchableOpacity>
          </VStack>
        </View>
      </ScrollView>
    </Box>
  );
}

export default ProfileScreen2;
