import React from "react";
import {
  ScrollView,
  Text,
  Box,
  Center,
  VStack,
  Image,
  HStack,
  Spacer,
} from "native-base";
import Colors from "../color";
import Buttone from "../Components/Buttone";
import Ionicons from "@expo/vector-icons";

const Inputs = [
  {
    Image: "NAME",
    alt: "text",
    icon:"Ionicons"
  },
];

function PaymentScreen() {
  return (
    <Box flex={1} safeArea bg={Colors.main} py={5}>
      <Center pb={15}>
        <Text color={Colors.white} fontSize={14} bold>
          PAYMENT METHOD
        </Text>
      </Center>
      <Box h="full" bg={Colors.subGreen} px={5}>
        <ScrollView showsVerticalScrollIndicator={false}>
          <VStack space={6} mt={5} pb={10}>
            <HStack
              alignItems="center"
              bg={Colors.white}
              px={3}
              py={1}
              rounded={10}
            >
              <Box>
                <Image
                  source={{
                    uri: "https://upload.wikimedia.org/wikipedia/commons/a/a4/Paypal_2014_logo.png",
                  }}
                  alt="paypal"
                  resizeMode="contain"
                  w={60}
                  h={50}
                />
                <Spacer />
              </Box>
              <Ionicons name="checkmark-circle" size={30} color />
            </HStack>
            <Buttone bg={Colors.main} color={Colors.white} mt={5}>
              CONTINUE
            </Buttone>
          </VStack>
        </ScrollView>
      </Box>
    </Box>
  );
}

export default PaymentScreen;
