import axios from "axios";
const URL = `http://192.168.1.6:5000`;
export const addBlog =
  (
    userId,
    category,
    title,
    image,
    image1,
    image2,
    description,
    price,
    city,
    district,
    ward
  ) =>
  async () => {
    try {
      const config = {
        headers: {
          "Content-Type": "application/json",
        },
      };

      const { data } = await axios.post(
        `${URL}/api/product`,
        {
          userId,
          category,
          title,
          image,
          image1,
          image2,
          description,
          price,
          city,
          district,
          ward,
        },
        config
      );
    } catch (error) {
      console.log(error);
    }
  };

