import React from "react";
import { View, Text, Box, ScrollView, FlatList } from "native-base";
import HomeSearch from "../Components/HomeSearch";
import HomeProducts from "../Components/HomeProducts";
import Colors from "../color";

const categories = [
  {
    label: "NAME",
    image: "https://images.summitmedia-digital.com/topgear/images/articleImages/news/0_2014/11/03/instagram_post/instagram-main1.jpg",
  },
  {
    label: "NAME",
    image: "text",
  },
  {
    label: "NAME",
    image: "text",
  },
  {
    label: "NAME",
    image: "text",
  },
];


function HomeScreen() {
  return (
    <Box flex={1} bg={Colors.gray}>
      <HomeSearch />
      <ScrollView showsVerticalScrollIndicator={false}>
        <Box bg={Colors.white} h={40} mt={2}>
          <Text bold p={3} fontSize={15}>
            Khám phá danh mục
          </Text>
          <ScrollView showsHorizontalScrollIndicator={false} horizontal={true} >

          </ScrollView>
        </Box>
      </ScrollView>
    </Box>
  );
}

export default HomeScreen;
